import { FC, useState } from 'react'
import { Bounce, toast } from 'react-toastify'
import { setTokenToSessionStorage } from '../../helpers/localStorage.helper'
import { useAppDispatch } from '../../hooks/hooks'
import { AuthService } from '../../servise/servise'
import { login } from '../../store/user/user.slice'
import styles from './AuthFrm.module.scss'

// Import img
import { AuthForm as AuthFormType } from '../../type/modal'
// import { Checkbox } from '../Chekcbox/Checkbox'
import cross from './cross.svg'

interface CheckboxProps {
	checked: boolean
	onChange: (checked: boolean) => void
}

export const Checkbox: FC<CheckboxProps> = ({ checked, onChange }) => {
	const handleCheckboxChange = () => {
		onChange(!checked) // меняем состояние
	}

	return (
		<label className={styles.button} htmlFor='myCheckbox'>
			<input
				className={styles.input_checkbox}
				type='checkbox'
				id='myCheckbox'
				checked={checked}
				onChange={handleCheckboxChange}
			/>
			<span
				className={`${styles.checkbox} ${checked ? styles.checked : ''}`}
			></span>
			<span className={styles.text}>
				Я даю согласие на обработку своих персональных данных и принимаю условия
				оферты
			</span>
		</label>
	)
}

export const AuthForm: FC<AuthFormType> = ({ isOpen, onClose }) => {
	// Checkbox
	const [isChecked, setIsChecked] = useState<boolean>(false)
	// Inputs Form
	const [username, setUsername] = useState<string>('')
	const [email, setEmail] = useState<string>('')
	const [password, setPassword] = useState<string>('')
	// Confirm password
	const [confirmPassword, setConfirmPassword] = useState<string>('')
	// Check authorized
	const [isLogin, setIsLogin] = useState<boolean>(true)
	const dispatch = useAppDispatch()

	if (!isOpen) return null

	const toggleForm = () => {
		setIsLogin(prev => !prev)
	}

	const registrationHandler = async (e: React.FormEvent<HTMLFormElement>) => {
		e.preventDefault()
		if (password !== confirmPassword) {
			toast.error('Пароли не совпадают')
			console.log(password.valueOf())
			return
		}
		try {
			const data = await AuthService.registration({
				email,
				password,
			})
			if (data) {
				toast.success('Account created')
				setIsLogin(true)
			}
		} catch (error: any) {
			const err = error.response?.data.message
			toast.error(err?.toString() || 'Ошибка при создании пользователя', {
				position: 'top-right',
				autoClose: 5000,
				hideProgressBar: false,
				closeOnClick: true,
				pauseOnHover: true,
				draggable: true,
				progress: undefined,
				theme: 'light',
				transition: Bounce,
			})
		}
	}

	const loginHandler = async (e: React.FormEvent<HTMLFormElement>) => {
		e.preventDefault()
		try {
			const data = await AuthService.login({
				email,
				password,
			})
			if (data) {
				setTokenToSessionStorage('token', data.token)
				dispatch(login(data))
				toast.success('Ваш вход успешен')
			}
		} catch (error: any) {
			const err = error.response?.data.message
			toast.error(err ? err.toString() : 'Ошибка при входе', {
				autoClose: 5000,
				hideProgressBar: false,
				closeOnClick: true,
				pauseOnHover: true,
				draggable: true,
				progress: undefined,
				theme: 'light',
				transition: Bounce,
			})
		}
	}

	// const handleCheckboxChange = () => {
	// 	setIsChecked(!isChecked)
	// }

	return (
		<div className={styles.box_auth}>
			<button onClick={onClose} className={styles.closeModal}>
				<img src={cross} alt='' />
			</button>
			<form
				onSubmit={isLogin ? loginHandler : registrationHandler}
				className={styles.form}
			>
				<h1 className={styles.title}>{isLogin ? 'ВХОД' : 'РЕГИСТРАЦИЯ'}</h1>
				<div className={styles.inputs}>
					<input
						onChange={e => setEmail(e.target.value)}
						id='email'
						className={styles.input_auth}
						placeholder='Почта'
						type='text'
					/>
					{!isLogin && (
						<input
							onChange={e => setUsername(e.target.value)}
							id='username'
							className={styles.input_auth}
							placeholder='Телеграмм'
							type='text'
						/>
					)}
					<input
						className={styles.input_auth}
						id='password'
						type='password'
						onChange={e => setPassword(e.target.value)}
						placeholder='Пароль'
					/>
					{!isLogin && (
						<input
							className={styles.input_auth}
							placeholder='Повторить пароль'
							type='password'
							onChange={e => setConfirmPassword(e.target.value)}
						/>
					)}
				</div>
				{isLogin && (
					<button className={styles.reset_pass}>Забыли пароль?</button>
				)}

				{!isLogin && (
					<div className={styles.rules}>
						<Checkbox checked={isChecked} onChange={setIsChecked} />
					</div>
				)}
				<button className={styles.send_form}>
					{isLogin ? 'Войти' : 'Зарегистрироваться'}
				</button>
				<p className={styles.question}>
					{isLogin ? 'Нет аккаунта?' : 'Уже есть аккаунт?'}
					<button
						type='button'
						onClick={toggleForm}
						className={styles.login_or_reg}
					>
						{isLogin ? 'Зарегистрироваться' : 'Войти'}
					</button>
				</p>
			</form>
		</div>
	)
}
